import React, {useState} from "react";
import {Link} from "react-router-dom";

function CreateDeck() {
    const initialState = { name: "", description: "" };
    const [formData, setFormData] = useState({initialState});

    const handleChange = ({target}) => {
        setFormData({...initialState, [target.name]: target.value})
    }

    const handleSubmit = () => {

    }




    function BreadCrumb() {
            return (
                <nav aria-label="breadcrumb">
                    <ol className="breadcrumb">
                        <li className="breadcrumb-item" key="0"><Link to="/">Home</Link></li>
                        <li className="breadcrumb-item active" aria-current="page" key="2">Create Deck</li>
                    </ol>
                </nav>
            )
        }


    return (
        <div>
            <BreadCrumb/>
            <h2>Create Deck</h2>
            <form onSubmit={handleSubmit}>
                <label htmlFor="name">
                        Name
                        <br/>
                    <input type="text" id="name" name="name" onChange={handleChange}></input>
                </label>
                <br/>
                <label htmlFor="description">
                        Description
                        <br/>
                    <textarea id="description" name="description" onChange={handleChange}></textarea>
                </label>
                <br/>
                <Link to="/" className="btn btn-primary">Cancel</Link>
                <button className="btn btn-primary">Submit</button>
            </form>
        </div>
    )
}


export default CreateDeck;
