import React, {useState} from "react";
import {Link} from "react-router-dom";
import DeckForm from "../components/DeckForm";

function CreateDeck() {
    const initialState = { name: "", description: "" };
    const [formData, setFormData] = useState({initialState});

    const handleChange = ({target}) => {
        setFormData({...initialState, [target.name]: target.value})

    }




    function BreadCrumb() {
            return (
                <nav aria-label="breadcrumb">
                    <ol className="breadcrumb">
                        <li className="breadcrumb-item" key="0"><Link to="/">Home</Link></li>
                        <li className="breadcrumb-item active" aria-current="page" key="2">Create Deck</li>
                    </ol>
                </nav>
            )
        }


    return (
        <div>
            <BreadCrumb/>
            <h2>Create Deck</h2>
            <form onSubmit={handleSubmit}>
             <DeckForm formData={formData} handleChange={handleChange}/>
            </form>
        </div>
    )
}


export default CreateDeck;
