import React, {useState, useEffect} from "react";
import { useParams, Link, Switch, Route } from "react-router-dom";
import {readDeck} from "../utils/api/index";
import Study from "../components/Study";


function DeckInfo(){
    const [cards, setCards] = useState([]);
    const [deck, setDeck] = useState({});
    const {deckId} = useParams();

    useEffect(() => {
            async function loadData() {
                try {
                    const data = await readDeck(deckId);
                    setDeck(data)
                    setCards(data.cards)
                    }
                catch(error) { 
                    console.log(error) 
                }
            }
        loadData();
    },[deckId])

     function BreadCrumb() {
            return (
                <nav aria-label="breadcrumb">
                    <ol className="breadcrumb">
                        <li className="breadcrumb-item" key="0"><Link to="/">Home</Link></li>
                        <li className="breadcrumb-item active" aria-current="page" key="2">{deck.name}</li>
                    </ol>
                </nav>
            )
        }

    return (

        <div>
            <BreadCrumb/>
            <h2>{deck.name}</h2>
            <p>{deck.description}</p>
            <Link to={`/decks/${deck.id}/edit`} className="btn btn-primary">Edit</Link>
            <Link to={`/decks/${deck.id}/study`} className="btn btn-primary">Study</Link>
            <Link to="#" className="btn btn-primary">Add Cards</Link>
            <Link to="#" className="btn btn-primary">Delete</Link>
            <br/>
            <br/>
            <h2>Cards</h2>
            {cards.map((card) => (
                <div className="card" key={card.id}>
                    <p className="card-text">{card.front}</p>
                    <p className="card-text">{card.back}</p>
                    <Link to="#" className="btn btn-primary">Edit</Link>
                    <Link to="#" className="btn btn-primary">Delete</Link>
                </div>
            ))}

        <Switch>
            <Route path={`/decks/:deckId/study`}>
                <Study/>
            </Route>
        </Switch>

        </div>
    )



}

export default DeckInfo;
